import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-alert-modal',
  templateUrl: './alert-modal.component.html',
  styleUrls: ['./alert-modal.component.scss']
})
export class AlertModalComponent {
  @Input() message: string;
  @Input() type: string;
}
<div class="p-3 d-flex align-items-center flex-column text-center">
  <br>
  <div class="modal-header">
    <img src="./assets/img/icons/alerta.svg" alt="Erro" width="80" height="80" *ngIf="type == 'error' ">
    <img src="./assets/img/icons/sucesso.svg" alt="Sucesso" width="80" height="80" *ngIf="type == 'success' ">
  </div>
  <hr>
  <div class="moda-body">
    <h6 [innerHTML]='message'></h6>
  </div>
</div>
