import { Component, OnInit } from '@angular/core';
import { User } from '../user';
import { QueryParameter } from 'src/app/models/query-parameter';
import { HttpCommonService } from 'src/app/services/app-http-service';
import { ActivatedRoute } from '@angular/router';
import { CommonService } from 'src/app/services/common.service';

@Component({
  selector: 'app-delete-user',
  templateUrl: './delete-user.component.html',
  styleUrls: ['./delete-user.component.scss']
})
export class DeleteUserComponent implements OnInit {
  // ATRIBUTTES
  public title: string;
  private rotaAnterior: string;
  private parameters: QueryParameter[];
  public label: string;
  private usuario: User;

  constructor(
    private service: HttpCommonService,
    private router: ActivatedRoute,
    private commonService: CommonService
  ) { }

  public ngOnInit(): void {
    this.initializeComponent();
    this.router.paramMap.subscribe((params) => {
      if (!this.commonService.isNullOrUndefined(params.get('id')) && params.get('id') !== '') {
        this.parameters = [
          { parameter: 'UserId', value: params.get('id') }
        ];
        this.service.getSingle('cadastros_url', 'user/getbyid', this.parameters)
          .toPromise()
          .then(c => {
            this.usuario = c;
            this.label = `Tem certerza que deseja deletar o item ${this.usuario.nome} ?`;
          })
          .catch(e => {
            this.commonService.responseActionWithNavigation(this.rotaAnterior, 'Houve um erro buscar o usuário.', false);
          });
      }
    });
  }

  public ngOnDestroy(): void {
    this.destroyComponent();
  }

  private initializeComponent(): void {
    this.title = 'Deletar usuário';
    this.label = '';
    this.rotaAnterior = './cadastros/test';
    this.parameters = [];
    this.usuario = new User();
  }

  private destroyComponent(): void {
    this.title = null;
    this.label = null;
    this.rotaAnterior = null;
    this.parameters = null;
    this.usuario = null;
  }

  public deletar(): void {
    this.service.delete('cadastros_url', 'user', this.usuario.identifier)
      .toPromise()
      .then(c => {
        this.commonService.responseActionWithNavigation
          (this.rotaAnterior, `Usuário<br>${this.usuario.nome}<br>Deletado com sucesso.`, true);
      })
      .catch(e => {
        this.commonService.responseActionWithNavigation(this.rotaAnterior, e.error, false);
      });
  }
}
<div class="col-md-12 col-lg-12 mb-4 align-items-stretch">
    <app-comp-title [title]="this.title"></app-comp-title>
    <div class="card border-0 rounded-0 h-100">
        <div class="card-title mb-1 p-3">
            <h5>{{this.label}}</h5>
        </div>
        <div class="card-body">
            <form>
                <button type="button" class="btn btn-lightning rounded-0 mb-2 mr-2" (click)="deletar()">
          Deletar
        </button>
                <button type="button" class="btn btn-outline-lightning rounded-0 mb-2" [routerLink]="['../../']">
          Cancelar
        </button>
            </form>
        </div>
    </div>
</div>
